package com.example.happyhoursimport android.os.Bundleimport androidx.appcompat.app.AppCompatActivityimport androidx.navigation.fragment.NavHostFragmentclass MainActivity : AppCompatActivity(R.layout.activity_main) {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        val navHostFragment =            supportFragmentManager.findFragmentById(R.id.nav_host_fragment_activity_main) as NavHostFragment        val navController = navHostFragment.navController        val navGraph = navController.navInflater.inflate(R.navigation.app_graph_main)        navGraph.setStartDestination(R.id.mainFlowFragment)        navController.graph = navGraph    }}