package com.example.happyhoursimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.os.PersistableBundleimport androidx.navigation.fragment.NavHostFragmentclass MainActivity : AppCompatActivity(R.layout.activity_main) {    override fun onCreate(savedInstanceState: Bundle?, persistentState: PersistableBundle?) {        super.onCreate(savedInstanceState, persistentState)        val navHostFragment =            supportFragmentManager.findFragmentById(R.id.nav_host_fragment_activity_main) as NavHostFragment        val navController = navHostFragment.navController        val navGraph = navController.navInflater.inflate(R.navigation.mobile_navigation)        navGraph.setStartDestination(R.id.authFlowFragment)        navController.graph = navGraph    }}